INSERT INTO `exp` (`id`, `expname`, `exptarget`, `expwork`, `expcodeurl`, `upid`, `createTime`, `expVideoPath`, `exampleDoc`, `flag`) VALUES
	(1, '数据获取', '实验依据网页爬虫原理，通过构造请求头headers将Python伪装成一个真实的浏览器，运用正则表达相关知识，对拟获取的天气数据进行匹配获取，让学生熟悉运用findall函数，会使用列表、字典、数据框等多种数据结构，完成数据的获取。', '请每位同学阅读示范代码，完成以下任务：\r\n第一，写出程序代码注释，解释程序内容；\r\n第二，结合爬取对象，阐述爬虫原理；\r\n第三，换成自己喜欢的城市，完成一次天气信息的爬取工作；\r\n第四，完成实验报告，内容包括对知识点的理解，对于出现错误的修正情况，以及其他方面的个人的体会。在系统提交程序代码、数据和实验报告。\r\n', '数据获取爬虫代码示范.py', NULL, NULL, 'get.mp4', 'get.docx', 1),
	(2, '数据预处理', '依据数据库原理、正态分布特征、空间向量映射和贝叶斯定理，通过对重复观测、缺失值、异常值、数据子集、数据透视、表的合并与连接、以及数据聚合，达到对已知数据集的清洗和预处理目标。让学生熟练运用Pandas模块的duplicated（）、isnull（）、fillna（）、pivot_table（）、concat（）等函数与方法。', '请每位同学阅读示范代码，完成以下任务：\r\n第一，以数据爬取实验中所爬取天气数据为数据集，判断数据质量，主要目标是查看是否存在缺失值，说明查找和判断缺失值的过程；如果存在缺失，请明确缺失的具体数据和数据特征，并分析问题形成的原因造成；给出缺失值处置方案并实践。\r\n第二，以dns_homework数据集为数据源，完成以下工作，并写出数据分析结果报告。\r\n1、该数据集的描述性统计；\r\n2、寻找重复性观测，并对其进行相应处理；\r\n3、查找缺失值，并对其进行相应处理；\r\n4、查找异常值，并对其进行相应处理.\r\n第三，完成实验报告，内容包括对实验过程的描述，对于出现错误的修正情况，以及其他方面的个人的体会。\r\n注：本实验是小组实验，由小组成员共同完成，在系统提交程序代码、数据和实验报告，并把成员名字和承担工作写入报告中。', '数据预处理示范代码.py', NULL, NULL, 'preparing.mp4', 'preparing.docx', 7),
	(3, '回归分析', '本实验依据数据库原理和回归分析原理，通过对数值型数据的线性回归建模、回归系数的计算、模型检验、模型诊断，实现连续型变量的预测。让学生熟练运用ols函数、predict方法、哑变量的设置、F统计量计算、t统计量计算、多重共线性检验等。', '实验任务：请每位同学阅读示范代码，完成以下任务：用数据爬取技术获取关于以“党和政府为疫情防控做实事”为主题的网络舆情的视频、图像和文本信息，构建数据集，结合线性回归模型建模、检验、预测、评估等步骤，进行如下预测分析：\r\n第一，中国疫情发展中确诊病例的人数与境内传播、境外输入、行政区域存在什么样可能的影响关系？\r\n第二，在某段清零的疫情管理中，染病人数的下降与境内传播、境外输入、政策执行力存在什么样可能的影响关系？完成实验报告，内容包括对实验过程的描述，对于出现错误的修正情况，以及其他方面的个人的体会。\r\n', '回归分析示范代码.py', NULL, NULL, 'regression.mp4', 'regression.docx', 3),
	(4, '数据可视化', '本实验依据数据库原理和描述性统计分析，通过对离散量、数值型单变量、多维数值关系的图形展示方法学习，达到对已知数据集的可视化处理。让学生熟练运用matplotlib、pandas和seaborn模块的图形函数与方法，并借助subplot2grid函数实现各种模块下图形的组合。', '请每位同学阅读示范代码，完成以下任务：\r\n第一，采用在数据爬取实验中自己爬取的天气数据，任选其中的4-6个变量，进行饼图、柱状图、散点图和热力图的实践应用分析。\r\n第二，对于给定数据集diomons进行可视化分析，并根据分析内容写出分析结论。\r\n完成实验报告，内容包括对实验过程的描述，对于出现错误的修正情况，以及其他方面的个人的体会。\r\n', '数据可视化示范代码.py', NULL, NULL, 'show.mp4', 'show.docx', 13),
	(5, '分类算法', '本实验依据数据库原理和回归分析原理，面向二分类的离散问题，构建Logistic回归模型，进行参数求解，并评估模型效果。让学生熟练运用LogisticRegression类、predict方法、正例负例覆盖比等。', '请每位同学阅读示范代码，完成以下任务：\r\n以给定数据集virtual_data为数据源，完成以下工作，并写出数据分析结果报告。\r\n第一，该数据集的描述性统计；\r\n第二，运用逻辑回归模型对其建模；\r\n第三，对回归模型求解参数；\r\n第四，进行模型效果评估；\r\n第五，根据数据计算结果，写出分析结论。\r\n完成实验报告，内容包括对实验过程的描述，对于出现错误的修正情况，以及其他方面的个人的体会。\r\n', '分类算法示范代码.py', NULL, NULL, 'classification.mp4', 'classification.docx', 2),
	(6, '聚类算法', '本实验依据Kmeans聚类、密度聚类和层次聚类原理，面向无监督问题，利用数据本身选出合理的k个簇，为数据集选择恰当的聚类方法。让学生熟练运用Kmeans类、拐点法、轮廓系数法、间隔统计量法、eps和min_samples调参等。', '请每位同学阅读示范代码，完成以下任务：\r\n第一，采用在数据获取实验中自己爬取的天气数据，任选其中的4-6个变量，进行聚类分析；\r\n第二，结合理论课的内容，完成数据读入、必要的标准化、三种方法进行最佳k值确定，根据确定的k值采用Kmeans聚类分析。\r\n第三，完成实验报告，内容包括对实验过程的描述，对于出现错误的修正情况，以及其他方面的个人的体会。\r\n', '聚类算法示范代码.py', NULL, NULL, 'cluster.mp4', 'cluster.docx', 1),
	(7, '决策树分析', '本实验依据决策树原理，采用“IF…THEN…”的思想对于数值型因变量预测和离散型因变量分类。让学生熟练掌握信息增益、误差降低法、代价复杂度法、CART树、Bootstrap抽样法等，进行节点字段的选择、剪枝技术、随机森林的实现思想等。', '请每位同学阅读示范代码，完成以下任务：以给定数据集titanic为数据源，完成以下工作，并写出数据分析结果报告。\r\n第一，该数据集的描述性统计；\r\n第二，运用决策树思想进行建模；\r\n第三，完成树的剪枝流程；\r\n第四，根据数据计算结果，写出分析结论。完成实验报告，内容包括对实验过程的描述，对于出现错误的修正情况，以及其他方面的个人的体会。\r\n', '决策树分析示范代码.py', NULL, NULL, 'desicionTree.mp4', 'desicionTree.docx', 3),
	(8, '关联分析算法', '本实验依据关联规则原理，利用一些度量指标来分辨数据库中存在的强规则，在大数据库中发现感兴趣的关系。让学生熟练掌握 Apriori算法候选集生成和情节的向下封闭检测两个阶段来挖掘频繁项集，和FP-Growth算法把数据集中的事务映射到一棵FP-Tree上面再根据这棵树找出频繁项集，并熟知两个算法的差异。 ', '请每位同学阅读示范代码，完成以下任务：以给定数据集knowledge为数据源，完成以下工作，并写出数据分析结果报告。\r\n第一，该数据集的描述性统计；\r\n第二，运用 Apriori算法思想进行关联规则分析；\r\n第三，运用 FP-Growth算法思想进行关联规则分析；\r\n第四，对比两个算法的数据计算结果，写出分析结论。\r\n', '关联分析算法示范代码.py', NULL, NULL, 'association.mp4', 'association.docx', 1),
